import React, { useState } from "react"
import styled from "styled-components"
import { Splide, SplideSlide } from '@splidejs/react-splide'
import '@splidejs/react-splide/css';

const Wrapper = styled.div`
margin: 4rem 0rem`
const Card = styled.div `
min-height: 25rem; 
border-radius: 2rem; 
overflow: hidden ;
img{ border-radius: 2rem} `
export default function Popular() {
const [popular , setPopular]=useState([])

async function getPpular(){
    const api = await fetch(`https://api.spoonacular.com/recipes/random?apiKey=${process.env.REACT_APP_API_KEY}&number=9&limitLicense=false`)
   const data = await api.json()
   setPopular(data.recipes)
   

}
React.useEffect(()=>{
    getPpular()
},[])

  return (
    <Wrapper> 
          <h3>Ppulaire Picks</h3>
          <Splide options={{pre}}>
          {popular.map((recipe:any)=>{
      return  ( 
        <SplideSlide><Card key={recipe.id}>
        <p>{recipe.title}</p>
        <img src={recipe.image}alt={recipe.title}></img>
        </Card></SplideSlide>)
    })
        }
          </Splide>
    
    </Wrapper>
  )
}
